export function AI(playerState) {
    var pos
    var max = 0
    var max_position
    var table = document.getElementById('gurumi')
    var cells = table.getElementsByClassName('image1')
    /*
    // Randomly pick a cell
    do {
        pos = Math.floor(Math.random() * 100)
    } while (playerState.positions[pos].probability === '-1')
    // Give a value
    playerState.positions[pos].probability = 200
    // Click highest
    for(var i = 0; i < 100; i++) {
        if (max < playerState.positions[i].probability) {
            max = playerState.positions[i].probability
            max_position = i
        }
    }
    console.log(max_position)

    if (max_position >= 0) {
        cells[max_position].click()
    }
    // Calculate adjacent
    calculateNextPos(playerState)
    */

    for(var i = 0; i < 100; i++) {
        if (max < playerState.positions[i].probability) {
            max = playerState.positions[i].probability
            max_position = i
        }
    }

    if(max === 0)
    {
        // Randomly pick a cell
        do {
            pos = Math.floor(Math.random() * 100)
        } while (playerState.positions[pos].probability === '-1')
        max_position = pos
    }
    
    if(max_position >= 0) {
        cells[max_position].click()
    }

    // calculateNextPos(playerState)
    calculateNextPos(playerState, max_position)
}

function calculateNextPos(playerState, current_position) {
    var randomness = 2
    var directions = { 'w': null, 'n': null, 'e': null, 's': null };
    var adjacent
    var nextToAdjacent

    // If it's a hit, pick from adjacent cell
    if(playerState.positions[current_position].hit) {
        for(var direction in directions){
            adjacent = playerState.positions[current_position][direction]
            nextToAdjacent = adjacent[direction]
            if(adjacent !== null)
            {
                adjacent.probability += Math.floor(50 * (Math.random() * randomness))
                if(nextToAdjacent !== null)
                {
                    nextToAdjacent.probability += Math.floor(25 * (Math.random() * randomness))
                }
            }
        }
    }

    playerState.positions[current_position].probability = -1
}

