export function AI(playerState) {
    var pos
    var max = 0
    var max_position
    var table = document.getElementById('gurumi')
    var cells = table.getElementsByClassName('image1')
    
    // Randomly pick a cell
    do {
        pos = Math.floor(Math.random() * 100)
    } while (playerState.positions[pos].probability === '-1')
    // Give a value
    playerState.positions[pos].probability = 200
    // Click highest
    for(var i = 0; i < 100; i++) {
        if (max < playerState.positions[i].probability) {
            max = playerState.positions[i].probability
            max_position = i
        }
    }
    console.log(max_position)

    if (max_position >= 0) {
        cells[max_position].click()
    }
    // Calculate adjacent
    calculateNextPos(playerState)
}

function calculateNextPos(playerState) {
    var randomness = 2
    var directions = { 'w': null, 'n': null, 'e': null, 's': null };
    for (var i = 0; i < 100; i++) {
        if(playerState.positions[i].fired) {
            if(playerState.positions[i].sunk && playerState.positions[i].probability !== -1) {
                for(var direction in directions){
                    playerState.positions[i][direction].probability += 50 * Math.floor(Math.random() * randomness)
                    if(playerState.positions[i][direction][direction] !== null) {
                        playerState.positions[i][direction][direction].probability += 25 * Math.floor(Math.random() * randomness)
                    }
                }
            }
            // Give -1 
            playerState.positions[i].probability = -1
        }
    }
}
