export function AI(playerState) {
    var pos
    var max = 0
    var max_position
    var table = document.getElementById('gurumi')
    var cells = table.getElementsByClassName('image1')

    /*
    // Pick the cell with highest prob
    for(var i = 0; i < 100; i++) {
        if (max < playerState.positions[i].probability) {
            max = playerState.positions[i].probability
            max_position = i
        }
    }

    // If there is no such cell
    if(max === 0)
    {
        // Randomly pick one
        do {
            pos = Math.floor(Math.random() * 100)
        } while (playerState.positions[pos].fired)
        max_position = pos
    }
    */

    playerState.positions[0].probability = 1

    for(var i = 0; i < 100; i++) {
        if (max < playerState.positions[i].probability) {
            max = playerState.positions[i].probability
            max_position = i
        }
    }
    
    if(max_position >= 0) {
        console.log(max_position)
        cells[max_position].click()
    }

    // calculateNextPos(playerState)
    playerState.positions[max_position].probability = -1
    calculateNextPos(playerState, max_position)
}

function calculateNextPos(playerState, current_position) {
    var randomness = 2
    var directions = { 'w': null, 'n': null, 'e': null, 's': null };
    var oppositeDirection
    var temp = 1

    if(playerState.positions[current_position].hit) {
        /*
        for(var direction in directions) {
            var adjacent = playerState.positions[current_position][direction]
            switch(direction)
            {
                case 'w':
                    oppositeDirection = 'e'
                    break
                case 'e':
                    oppositeDirection = 'w'
                    break
                case 'n':
                    oppositeDirection = 's'
                    break
                case 's':
                    oppositeDirection = 'n'
                    break
            }
            if(adjacent && adjacent.probability >= 0){
                var random1 = Math.floor(50 * (Math.random() * (randomness - 1) + 1))
                console.log("Random with 50 as base : ", random1)
                adjacent.probability += random1

                var nextToAdjacent = adjacent[direction]
                var temp2 = adjacent[oppositeDirection]
                if(temp2){
                    while(temp2 && temp2.hit && !temp2.sunk && temp2.probability + temp === 0){
                        temp += 1
                        temp2 = temp2[oppositeDirection]
                    }
                }
                

                if(nextToAdjacent && nextToAdjacent.probability >= 0){
                    var random2 = Math.floor(25 * (Math.random() * (randomness - 1) + 1))
                    console.log("Random with 25 as base : ", random2)
                    nextToAdjacent.probability += temp * random2
                }
            }
        }
        */
        var adjacent = playerState.positions[current_position]['s']
        var temp2 = playerState.positions[current_position]['n']
        if(temp2){
            while(temp2 && temp2.hit && !temp2.sunk){
                temp += 1
                temp2 = temp2['n']
            }
        }

        playerState.positions[current_position].probability = 2
    }
}
