export function AI(playerState) {
    var pos
    var max = 0
    var max_position
    var table = document.getElementById('gurumi')
    var cells = table.getElementsByClassName('image1')

    for(var i = 0; i < 100; i++) {
        if (max < playerState.positions[i].probability) {
            max = playerState.positions[i].probability
            max_position = i
        }
    }

    if(max === 0)
    {
        // Randomly pick a cell
        do {
            pos = Math.floor(Math.random() * 100)
        } while (playerState.positions[pos].probability === '-1')
        max_position = pos
    }
    
    if(max_position >= 0) {
        cells[max_position].click()
    }

    // calculateNextPos(playerState)
    calculateNextPos(playerState, max_position)
}

function calculateNextPos(playerState, current_position) {
    var randomness = 2
    var directions = { 'w': null, 'n': null, 'e': null, 's': null };
    var oppositeDirection
    var temp = 1

    // If it's a hit, pick from adjacent cell
    if(playerState.positions[current_position].hit) {
        for(var direction in directions){
            var adjacent = playerState.positions[current_position][direction]
            if(adjacent && adjacent.probability !== -1)
            {
                adjacent.probability += Math.floor(50 * (Math.random() * randomness))

                switch(direction)
                {
                    case 'w' : 
                        oppositeDirection = 'e'
                        break
                    case 'n' : 
                        oppositeDirection = 's'
                        break
                    case 'e' : 
                        oppositeDirection = 'w'
                        break
                    case 's' : 
                        oppositeDirection = 'n'
                        break
                }

                if(adjacent[oppositeDirection] && adjacent[oppositeDirection].hit && !adjacent[oppositeDirection].sunk) // ข้าง ๆ โดนเลือกแต่ยังไม่จม -> อาจเป็นเรือเดิมที่เพึ่งเลือก
                {
                    temp += 1
                }

                var nextToAdjacent = playerState.positions[current_position][direction][direction]
                if(nextToAdjacent && adjacent.probability !== -1)
                {
                    nextToAdjacent.probability += temp * Math.floor(25 * (Math.random() * randomness))
                }
            }
        }
    }

    playerState.positions[current_position].probability = -1
}

function randomDirection (directions) {
    var direction = Object.keys(directions);
    var pickedDirection = directions[direction[ direction.length * Math.random() << 0]];
    return pickedDirection
};

