import Image from './Image'
import Container from 'react-bootstrap/Container'
import { useEffect, useState } from 'react'

const Table = (props) => {

    const player = Object.freeze({
        name: props.player,
        cells: [],
        ship: [],
        founded: 0,
    })
    const [playerState, setPlayerState] = useState(player)

    var gameover = false

    function getRandomInt(min, max) {
        const minimum = Math.ceil(min);
        const maximum = Math.floor(max);
     
        return Math.floor(Math.random() * (maximum - minimum + 1)) + minimum;
     }
    
    function randomShip(min, max, excludeArrayNumbers) {
        let randomNumber;
      
        if(!Array.isArray(excludeArrayNumbers)) {
          randomNumber = getRandomInt(min, max);
          return randomNumber;
        }
      
        do {
          randomNumber = getRandomInt(min, max);
        } while ((excludeArrayNumbers || []).includes(randomNumber));
      
        return randomNumber;
    }

    function play(e) {
        console.log(e)
        checkShip(player, e)
        
        if(gameover){
            end(props.player)
        }
    }

    function checkShip(player, e) {
        if(player.ship.includes(e.target)){
            player.founded = player.founded + 1
            player.point.innerHTML = 'Founded ship: ' + player.founded
            if(player.founded === 10) {
                gameover = true
            }
            foundShip(player, e)
        }
        else {
            notFound(player, e)
        }
    }

    function notFound(player, e) {
        console.log(player.name + ': nothing here...')
        e.target.src = 'https://tmsvalue.co.uk/wp-content/uploads/2017/03/Square-500x500-red.png'
    }
    
    function foundShip(player, e) {
        console.log(player.name + ': FOUND SHIP!!!')
        e.target.src = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR9qcrQeLufxv61jZ194tG5CJvux0p4U4-r2g&usqp=CAU'
    }
    
    function end(player) {
        console.log(player + 'WIN!!!')
    }

    function generateShip() {
        for(let i = 0;i < 10;i++){
            setPlayerState({
                ...playerState,
                ship: player.ship.push(randomShip(0, 24, player.ship))
            })
            console.log(player.ship)
        }
    }

    useEffect(() => {
        generateShip()
    }, [setPlayerState])

    return (
        <Container>
            <h2>{props.player}</h2>
            <table>
                {Array.from({ length: 10 }).map((_, row) => {
                    return (
                        <tr>
                            {Array.from({ length: 10 }).map((_, col) => {
                                var index = ((10 - row - 1) * 10) + (10 - col)
                                var src = 'map/' + index + '.jpg'
                                return (
                                    <td key={index} className='cell' onClick={play} ><Image value={index} src={src} /></td>
                                )
                            })}
                        </tr>
                    )
                })}
            </table>
        </Container>
    )
}

export default Table
